import React, { useState, useEffect, useRef } from 'react';
import Pesan from './Pesan';
const style = {
  main: `flex flex-col p-[10px] relative`,
};

const Chat = () => {
  const [pesan,setPesan] = useState()
  const scroll = useRef();

  return (
    <>
      <main className={style.main}>
        <Pesan />
      </main>
      {/* Send Component */}
      <span ref={scroll}></span>
    </>
  );
};

export default Chat;
